package Fundamentals;

public class StringDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
String Str="hyderabad";
System.out.println("String length="+Str.length());
System.out.println("o Index character="+Str.charAt(0));
System.out.println("d character index value="+Str.indexOf('d'));
System.out.println("bad is there in str="+Str.contains("bad"));
System.out.println("subString value="+Str.substring(6,9));
System.out.println("upper case="+Str.toUpperCase());
System.out.println("lower case="+Str.toLowerCase());
System.out.println("hello"+Str);
String Str1="hi";
System.out.println(Str1);
System.out.println(Str.trim());

	}

}
