package corejava;
import java.lang.Integer;
public class SumOf{

	public static void main(String args[]) {
		// TODO Auto-generated method stub
		int x=Integer.parseInt(args[0]);
		int y=Integer.parseInt(args[1]);
		int sum=x+y;
		System.out.println("the sum of x and y is:"+sum);

	}

}
